CREATE DATABASE geodatabase;

CREATE TABLE coordinates(
    id SERIAL PRIMARY KEY,
    latitude float,
    longitude float,
    timedate TIMESTAMP
);

CREATE TABLE building(
    id SERIAL PRIMARY KEY,
    name TEXT,
    latitude float,
    longitude float
);



DROP TABLE coordinates
DROP TABLE building

